var Minimatch=require("../minimatch.js").Minimatch,tap=require("tap");tap.test("cache test",function(e){var t=new Minimatch("a?b"),n=new Minimatch("a?b");e.equal(t,n,"should get the same object");for(var r=0;r<100;r++)new Minimatch("a"+r);n=new Minimatch("a?b");e.notEqual(t,n,"cache should have dropped");e.end()});